import React, { useState } from "react";
import { AuthProvider } from "./AuthContext";
import ItemList from "./ItemList";

const initialItems = [
  { id: 1, name: "Screwdriver", description: "Flathead screwdriver", quantity: 10 },
  { id: 2, name: "Hammer", description: "16oz claw hammer", quantity: 5 },
  { id: 3, name: "Wrench", description: "Adjustable wrench", quantity: 7 },
];

function App() {
  const [items, setItems] = useState(initialItems);

  const handleDelete = (id) => {
    setItems(items.filter(item => item.id !== id));
  };

  const handleEdit = (item) => {
    alert(`Edit item: ${item.name}`);
    // Here you would open a modal or form to edit the item
  };

  return (
    <AuthProvider>
      <h2>Inventory List</h2>
      <ItemList items={items} onDelete={handleDelete} onEdit={handleEdit} />
    </AuthProvider>
  );
}

export default App;
