import React from "react";
import { Link, useLocation } from "react-router-dom";
import { FaChartPie, FaBoxOpen, FaUserCircle } from "react-icons/fa";
import { useAuth } from "./AuthContext";
export default function Dashboard() {
  const { user } = useAuth();
  const location = useLocation();
  const menuLinks = [
    { to: "/dashboard", icon: <FaChartPie />, label: "Dashboard" },
    { to: "/items", icon: <FaBoxOpen />, label: "Items" },
    { to: "/profile", icon: <FaUserCircle />, label: "Profile" }
  ];
  return (
    <div>
      <nav style={{
        width: 220, background: "#222e3c", color: "#fff", minHeight: "100vh",
        position: "fixed", top: 0, left: 0, paddingTop: 16
      }}>
        <ul style={{ listStyle: "none", padding: 0, margin: 0 }}>
          {menuLinks.map(link =>
            <li key={link.to}>
              <Link
                to={link.to}
                style={{
                  display: "flex", alignItems: "center", padding: "12px 24px",
                  color: "#fff", textDecoration: "none",
                  background: location.pathname === link.to ? "#26b99a" : "none"
                }}
              >
                {link.icon} <span style={{ marginLeft: 12 }}>{link.label}</span>
              </Link>
            </li>
          )}
        </ul>
      </nav>
      <div style={{ marginLeft: 220, padding: 40 }}>
        <header style={{
          display: "flex", alignItems: "center", justifyContent: "space-between",
          background: "#fff", borderBottom: "1px solid #eee", height: 60, marginBottom: 20
        }}>
          <div style={{ fontWeight: 700, fontSize: 22, color: "#26b99a" }}>Inventory Dashboard</div>
          <Link to="/profile" style={{ color: "#222", textDecoration: "none", display: "flex", alignItems: "center" }}>
            <span>{user?.username} ({user?.role})</span>
            <FaUserCircle size={32} style={{ marginLeft: 10 }} />
          </Link>
        </header>
        <div>
          <h2>Welcome, {user?.username}!</h2>
          <p>This is your dashboard.</p>
        </div>
      </div>
    </div>
  );
}
