import React from "react";
import BackToDashboardButton from "../components/BackToDashboardButton";

const items = [
  {
    id: 1,
    name: "Hanswooly Cotton Casual Shirt",
    sku: "SKU-001",
    image: "https://img.icons8.com/color/96/000000/jacket.png",
    quantity: 45,
    price: 29.99,
  },
  {
    id: 2,
    name: "Cutiepie Rompers",
    sku: "SKU-002",
    image: "https://img.icons8.com/color/96/000000/baby-clothes.png",
    quantity: 20,
    price: 19.99,
  },
  {
    id: 3,
    name: "Classic Jeans",
    sku: "SKU-003",
    image: "https://img.icons8.com/color/96/000000/jeans.png",
    quantity: 35,
    price: 39.99,
  },
  {
    id: 4,
    name: "Sports Shoes",
    sku: "SKU-004",
    image: "https://img.icons8.com/color/96/000000/trainer.png",
    quantity: 17,
    price: 59.99,
  },
];

export default function Items() {
  return (
    <div style={{ padding: 40, maxWidth: 900, margin: "0 auto" }}>
      <h2 style={{ marginBottom: 28 }}>Available Items</h2>
      <div style={{
        display: "flex",
        flexWrap: "wrap",
        gap: 24,
        justifyContent: "flex-start"
      }}>
        {items.map(item => (
          <div
            key={item.id}
            style={{
              background: "#fff",
              borderRadius: 10,
              boxShadow: "0 1px 6px rgba(60,72,86,.08)",
              padding: 22,
              minWidth: 220,
              flex: "0 0 220px",
              textAlign: "center",
              position: "relative",
            }}
          >
            <img
              src={item.image}
              alt={item.name}
              style={{
                width: 72,
                height: 72,
                objectFit: "contain",
                marginBottom: 12,
                borderRadius: 8,
              }}
            />
            <div style={{ fontWeight: 600, fontSize: 16, marginBottom: 6 }}>
              {item.name}
            </div>
            <div style={{ color: "#888", fontSize: 13, marginBottom: 6 }}>
              {item.sku}
            </div>
            <div style={{ fontSize: 15, marginBottom: 6 }}>
              <span style={{ color: "#26b99a", fontWeight: 600 }}>
                {item.quantity}
              </span>{" "}
              in stock
            </div>
            <div style={{ fontSize: 16, fontWeight: 700, color: "#5e35b1" }}>
              ${item.price.toFixed(2)}
            </div>
          </div>
        ))}
      </div>
      <BackToDashboardButton />
    </div>
  );
}
