import React from "react";
import PropTypes from "prop-types";
import { useAuth } from "./AuthContext";

export default function ItemList({ items = [], onDelete, onEdit }) {
  const { role } = useAuth();

  if (!Array.isArray(items)) {
    // Optionally log or handle error
    return <tbody><tr><td colSpan="5">No items to display.</td></tr></tbody>;
  }

  return (
    <table border="1" cellPadding="8" style={{ margin: "20px 0" }}>
      <thead>
        <tr>
          <th>Name</th>
          <th>Description</th>
          <th>Quantity</th>
          {role === "manager" && <th>Actions</th>}
          {role === "staff" && <th>Edit</th>}
        </tr>
      </thead>
      <tbody>
        {items.length === 0 ? (
          <tr>
            <td colSpan={role === "manager" || role === "staff" ? 4 : 3}>No items found.</td>
          </tr>
        ) : (
          items.map(item => (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>{item.description}</td>
              <td>{item.quantity}</td>
              {role === "manager" && (
                <td>
                  <button onClick={() => onEdit(item)}>Edit</button>
                  <button onClick={() => onDelete(item.id)}>Delete</button>
                </td>
              )}
              {role === "staff" && (
                <td>
                  <button onClick={() => onEdit(item)}>Edit</button>
                </td>
              )}
            </tr>
          ))
        )}
      </tbody>
    </table>
  );
}

ItemList.propTypes = {
  items: PropTypes.array,
  onDelete: PropTypes.func,
  onEdit: PropTypes.func
};
