import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Items() {
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState({ name: "", quantity: "", price: "" });
  const [error, setError] = useState("");

  useEffect(() => {
    axios.get("http://localhost:5000/api/items")
      .then(res => setItems(res.data))
      .catch(err => {
        setError("Failed to fetch items. Is the backend running?");
        console.error(err);
      });
  }, []);

  const handleAdd = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/items", {
        name: newItem.name,
        quantity: Number(newItem.quantity),
        price: Number(newItem.price)
      });
      setItems([...items, response.data]);
      setNewItem({ name: "", quantity: "", price: "" });
      setError("");
    } catch (err) {
      setError("Failed to add item. Please check your connection and try again.");
      console.error(err);
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: "40px auto" }}>
      <h2>Items</h2>
      {error && (
        <div style={{ color: "red", marginBottom: 16 }}>
          {error}
        </div>
      )}
      <form onSubmit={handleAdd} style={{ marginBottom: 24 }}>
        <input
          placeholder="Name"
          value={newItem.name}
          onChange={e => setNewItem({ ...newItem, name: e.target.value })}
          required
        />
        <input
          type="number"
          placeholder="Quantity"
          value={newItem.quantity}
          onChange={e => setNewItem({ ...newItem, quantity: e.target.value })}
          required
        />
        <input
          type="number"
          placeholder="Price"
          value={newItem.price}
          onChange={e => setNewItem({ ...newItem, price: e.target.value })}
          required
        />
        <button type="submit">Add Item</button>
      </form>
      <table border="1" cellPadding="8" width="100%">
        <thead>
          <tr>
            <th>Name</th><th>Quantity</th><th>Price</th>
          </tr>
        </thead>
        <tbody>
          {items.map(item =>
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>{item.quantity}</td>
              <td>{item.price}</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
